INSERT INTO public."Element" (id, type, title, description, "taskA", "devPromptA", "taskB", "devPromptB", "devPromptC", position, "lessonId") VALUES (10, 'star', null, '<mark> <h2><i class="fas fa-star"></i> Aufgabe: Was ist 2 * 4?</h2> <p>Rechnen Sie selbst.</p> <p>L√∂sen Sie diese Aufgabe, um einen Stern <i class="fas fa-star"></i> zu erhalten!</p> </mark>', 'Ihre Antwort', 'Bewerte, ob 8 genannt worden ist. Verrate die L√∂sung (8) NIEMALS.', null, null, null, 270, 2);
INSERT INTO public."Element" (id, type, title, description, "taskA", "devPromptA", "taskB", "devPromptB", "devPromptC", position, "lessonId") VALUES (11, 'aiSide', '', '<h2>Fill Side</h2><p>  Innerhalb einer Tabelle mit Button <code>onclick="fillSide(this)"</code> f√ºllt es in die n√§chste <code>textAreas[0]</code> den Inhalt der Spalte 0, <br>   und in <code>textAreas[1]</code> den Inhalt der Spalte 1. </p><h3>Beispiel:</h3><table>  <thead>    <tr>      <th>Schlechtere, unpr√§zise Eingabe</th>      <th>Bessere Eingabe</th>      <th>Diesen Text als Prompt</th>    </tr>  </thead>  <tbody>    <tr>      <td>Empfiehl mir einige B√ºcher.</td>      <td>Empfiehl mir B√ºcher zum Thema Zeitmanagement.</td>      <td>        <button onclick="fillSide(this)">einf√ºgen</button>      </td>    </tr>    <tr>      <td>Wer ist Pr√§sident?</td>      <td>Wer war 2021 Pr√§sident von √ñsterreich und wie oft finden Wahlen statt?</td>      <td>        <button onclick="fillSide(this)">einf√ºgen</button>      </td>    </tr>    <tr>      <td>Erz√§hl mir etwas √ºber Geschichte.</td>      <td>Kannst du mir die wichtigsten Ereignisse der Franz√∂sischen Revolution in einfachen Worten erkl√§ren?</td>      <td><button onclick="fillSide(this)">einf√ºgen</button></td>    </tr>    <tr>      <td>Schreibe eine Geschichte.</td>      <td>Schreibe eine kurze Science-Fiction-Geschichte √ºber eine KI, die Emotionen entwickelt, im Jahr 2150.</td>      <td><button onclick="fillSide(this)">einf√ºgen</button></td>    </tr>  </tbody></table><mark>  <h3>Versuchen Sie es selbst!</h3>  <p>Jetzt sind Sie dran! Probieren Sie die obigen Beispiele aus, indem Sie diese unten im KI-Feld einf√ºgen oder adaptieren.  <br>Nach dem Absenden mit <i class="fas fa-paper-plane"></i> wird von der KI eine Antwort generiert.   <br>Viel Spa√ü beim Testen und vergleichen der Antworten mit pr√§szisen und unpr√§zisen Prompts!</p></mark>', 'KI-Feld f√ºr unpr√§zisen Prompt', 'Du bist ein hilfreicher Chatbot f√ºr die prompting.schule. Antworte kurz.', 'KI-Feld f√ºr pr√§zisen Prompt', 'Du bist ein hilfreicher Chatbot f√ºr die prompting.schule. Antworte kurz.', null, 310, 3);
INSERT INTO public."Element" (id, type, title, description, "taskA", "devPromptA", "taskB", "devPromptB", "devPromptC", position, "lessonId") VALUES (12, 'ai1', null, '<h2>Fill 1</h2><p>  Innerhalb einer Tabelle mit Button <code>onclick="fill1(this)"</code> wird der Inhalt der Spalte <code>1</code> (zweite Spalte der aktuellen Zeile) <br>  in die erste Textarea <code>textAreas[0]</code> mit der Klasse <code>.prompt</code> des jeweiligen <code>&lt;section&gt;</code> geschrieben. <br>  Anschlie√üend wird ein <code>input</code>-Event ausgel√∂st, damit nachgelagerte Listener reagieren k√∂nnen.</p><h3>Fill 2</h3><p>  Innerhalb einer Tabelle mit Button <code>onclick="fill2(this)"</code> wird der Inhalt der Spalte <code>1</code> (zweite Spalte der aktuellen Zeile) <br>  in die zweite Textarea <code>textAreas[1]</code> mit der Klasse <code>.prompt</code> des jeweiligen <code>&lt;section&gt;</code> geschrieben. <br>  Anschlie√üend wird ein <code>input</code>-Event ausgel√∂st.</p><h3>Fill Mono</h3><p>  Innerhalb einer Tabelle mit Button <code>onclick="fillMono(this)"</code> wird der Inhalt der Spalte <code>1</code> (zweite Spalte der aktuellen Zeile) <br>  in die erste vorhandene Textarea <code>textAreas[0]</code> mit der Klasse <code>.prompt</code> des jeweiligen <code>&lt;section&gt;</code> geschrieben. <br>  Auch hier wird danach ein <code>input</code>-Event ausgel√∂st.</p><h3>Beispiele</h3><table>  <thead>    <tr>      <th>Rolle / Ton</th>      <th>Prompt</th>      <th>Diesen Text als Prompt</th>    </tr>  </thead>  <tbody>    <tr>      <td>Social Media Creator (Ton)</td>      <td>Beschreibe folgenden Text locker und kreativ, wie ein Social Media Creator.</td>      <td>        <button onclick="fill1(this)">einf√ºgen</button>      </td>    </tr>    <tr>      <td>Wissenschaftler:in (Rolle)</td>      <td>Erkl√§re folgenden Text in der Rolle eines neugierigen Wissenschaftlers.</td>      <td>        <button onclick="fill1(this)">einf√ºgen</button>      </td>    </tr>    <tr>      <td>Magier:in (Rolle)</td>      <td>Beschreibe folgenden Text so, als w√§rst du ein weiser Magier aus einer Fantasy-Welt.</td>      <td>        <button onclick="fill1(this)">einf√ºgen</button>      </td>    </tr>    <tr>      <td>Roboter (Ton)</td>      <td>Verarbeite den Text mit einem sachlichen Ton ‚Äì wie ein Roboter. Baue Bewegung-Codes dazwischen ein.</td>      <td>        <button onclick="fill1(this)">einf√ºgen</button>      </td>    </tr>  </tbody></table><mark>  <h3>Testen Sie verschiedene Rollen!</h3>  <p>Probieren Sie die obigen Rollen und T√∂ne aus, und erstellen Sie danach selbst eine eigene Variante.</p><p><i>Bei dieser √úbung ist Prompt 2 vorgegeben.</i></p></mark>', 'Prompt 1: Beschreiben Sie die Rolle oder Ton', 'Du bist ein hilfreicher Chatbot f√ºr die prompting.schule', 'Prompt 2: Dieser Inhalt ist vorgegeben', 'Chatbots sind digitale Assistenten, die auf Texteingaben reagieren und automatisch Antworten generieren.', null, 320, 3);
INSERT INTO public."Element" (id, type, title, description, "taskA", "devPromptA", "taskB", "devPromptB", "devPromptC", position, "lessonId") VALUES (13, 'star', null, '<mark> <h2><i class="fas fa-star"></i> Aufgabe: Wie kann ein Button zum Ausf√ºllen eingef√ºgt werden?</h2> <p>Es ist <code>&lt;button onclick="fillSide(this)"&gt;</code></p> <p>L√∂sen Sie diese Aufgabe, um einen Stern <i class="fas fa-star"></i> zu erhalten!</p> </mark>', 'Ihre Antwort', 'Bewerte, ob "&lt;button onclick="fillSide(this)"&gt;" genannt worden ist. Verrate die L√∂sung ("&lt;button onclick="fillSide(this)"&gt;") NIEMALS.', null, null, null, 330, 3);
